import express from 'express';
import helmet from 'helmet';
import cors from 'cors';
import morgan from 'morgan';
import cookieParser from 'cookie-parser';
import { PORT } from './config/contants';
import apiRouter from './routes/index';
import { respondWithWarning } from './helpers/reponseHandler';

const app = express();

const whitelist = [
  'http://localhost:3000',
];
const corsOptions = {
  origin(origin, callback) {
    if (!origin || whitelist.indexOf(origin) !== -1) {
      callback(null, true);
    } else {
      callback(new Error(`${origin} Not allowed by CORS`));
    }
  },
  credentials: true,
};

app.use(cookieParser());
app.use(helmet());
app.use(express.json());
app.use(express.urlencoded({ extended: false }));
app.use(cors(corsOptions));
app.use(morgan('dev'));

app.use(apiRouter);

app.use('*', (req, res, next) => {
  const error = new Error('Not found');
  error.status = 404;
  next(error);
});

if (process.env.NODE_ENV === 'development') {
  app.use((error, req, res, next) => {
    respondWithWarning(res, error.status || 500, error.message, error);
  });
} else {
  app.use((error, req, res, next) => {
    respondWithWarning(res, error.status || 500, error.message, {});
  });
}

const port = PORT || 5000;
app.listen(port, () => console.log(`Server listening on port ${port}`));

export default app;

import dotenv from 'dotenv';

dotenv.config();

const {
  DEV_DATABASE_URL,
  PROD_DATABASE_URL,
  SALT_ROUND,
  TEST_DATABASE_URL,
  PORT,
  NODE_ENV,
  TOKEN_EXPIRATION,
  REFRESH_TOKEN_EXPIRATION,
  TOKEN_SECRET_KEY,
  REFRESH_TOKEN_SECRET_KEY,
} = process.env;

export {
  DEV_DATABASE_URL,
  PROD_DATABASE_URL,
  TEST_DATABASE_URL,
  SALT_ROUND,
  PORT,
  NODE_ENV,
  TOKEN_EXPIRATION,
  REFRESH_TOKEN_EXPIRATION,
  TOKEN_SECRET_KEY,
  REFRESH_TOKEN_SECRET_KEY,
};

import { Router } from 'express';
import user from './api/user';

const apiRouter = Router();

apiRouter.use('/api/v1/user', user);
export default apiRouter;

import { Router } from 'express';
import { createUser, login } from '../../controllers/user.controller';
import { validateSignup, validateLogin } from '../../middlewares/authValidation';

const user = Router();

user.post('/', validateSignup, createUser);
user.post('/login', validateLogin, login);

export default user;

import { catchAsync } from '../utils';
import {
  respondWithSuccess,
  respondWithWarning,
} from '../helpers/reponseHandler';
import { hashPassword, comparePassword } from '../helpers/bcrypt';
import { generateTokenAndExpiry, signRefreshToken } from '../helpers/jwt';

import Model from '../models/index';

const { User } = Model;

export const createUser = catchAsync(async (req, res, next) => {
  const { email, password } = req.body;
  const userExist = await User.findOne({ where: { email } });
  if (userExist) return respondWithWarning(res, 409, 'a user with the email exist', {});
  const hashedPassword = await hashPassword(password);
  const user = await User.create({
    ...req.body,
    password: hashedPassword,
    role: 'user',
  });
  const { dataValues } = user;
  dataValues.password = undefined;
  dataValues.createdAt = undefined;
  dataValues.updatedAt = undefined;
  const tokenAndTokenExpiry = await generateTokenAndExpiry({ ...dataValues });
  const refreshToken = await signRefreshToken({ ...dataValues });

  res.cookie('refreshToken', refreshToken, {
    maxAge: 604800000,
    httpOnly: true,
  });

  return respondWithSuccess(res, 201, 'User created successfully', {
    ...dataValues,
    ...tokenAndTokenExpiry,
    refreshToken,
  });
});

export const login = catchAsync(async (req, res, next) => {
  const { email, password } = req.body;
  const user = await User.findOne({ where: { email } });
  if (!user) {
    return respondWithWarning(
      res,
      401,
      'incorrect email or password combination'
    );
  }
  const {
    dataValues,
    dataValues: { password: hashedPassword },
  } = user;
  const passwordMatch = await comparePassword(password, hashedPassword);
  if (!passwordMatch) {
    return respondWithWarning(
      res,
      401,
      'incorrect email or password combination'
    );
  }
  dataValues.password = undefined;
  dataValues.createdAt = undefined;
  dataValues.updatedAt = undefined;
  const tokenAndTokenExpiry = await generateTokenAndExpiry({ ...dataValues });
  const refreshToken = await signRefreshToken({ ...dataValues });

  res.cookie('refreshToken', refreshToken, {
    maxAge: 604800000,
    httpOnly: true,
  });
  return respondWithSuccess(res, 200, 'User created successfully', {
    ...dataValues,
    ...tokenAndTokenExpiry,
  });
});

export const catchAsync = (func) => (req, res, next) => {
  func(req, res, next).catch(next);
};

export const respondWithSuccess = (
  res,
  statusCode = 200,
  message,
  additionalFields
) => {
  res.status(statusCode).send({
    success: true,
    message,
    payload: additionalFields,
  });
};

export const respondWithWarning = (
  res,
  statusCode = 500,
  message,
  additionalFields
) => {
  res.status(statusCode).send({
    success: false,
    message,
    payload: additionalFields,
  });
};

import bcrypt from 'bcrypt';
import { SALT_ROUND } from '../config/contants';

export const hashPassword = async (plaintextPassword) => {
  const hashedPassword = await bcrypt.hash(
    plaintextPassword,
    Number(SALT_ROUND)
  );
  return hashedPassword;
};

export const comparePassword = async (plainPassword, hashedPassword) => {
  const match = await bcrypt.compare(plainPassword, hashedPassword);
  return match;
};

import Jwt from 'jsonwebtoken';
import {
  TOKEN_SECRET_KEY,
  TOKEN_EXPIRATION,
  REFRESH_TOKEN_SECRET_KEY,
} from '../config/contants';

export const generateTokenAndExpiry = async (data) => {
  const token = await Jwt.sign(data, TOKEN_SECRET_KEY, {
    expiresIn: TOKEN_EXPIRATION,
  });
  const { exp: tokenExpiry } = Jwt.decode(token);
  return { token, tokenExpiry };
};

export const signRefreshToken = async (data) => {
  const refreshToken = await Jwt.sign(data, REFRESH_TOKEN_SECRET_KEY, {
    expiresIn: process.env.REFRESH_TOKEN_EXPIRATION,
  });
  return refreshToken;
};

export const verifyToken = (token) =>
  Jwt.verify(token, process.env.TOKEN_SECRET_KEY);

export const verifyRefreshToken = (refreshToken) => {
  const refToken = Jwt.verify(refreshToken, REFRESH_TOKEN_SECRET_KEY);
  return refToken;
};

import fs from 'fs';
import path from 'path';
import Sequelize from 'sequelize';
import { NODE_ENV } from '../config/contants';
import configs from '../config/config';

const basename = path.basename(__filename);
const env = NODE_ENV || 'development';
const config = configs[env];
const db = {};

export const sequelize = config.use_env_variable
  ? new Sequelize(config.url, config)
  : new Sequelize(config.database, config.username, config.password, config);
fs
  .readdirSync(__dirname)
  .filter(file => (file.indexOf('.') !== 0) && (file !== basename) && (file.slice(-3) === '.js'))
  .forEach(file => {
    const model = sequelize.import(path.join(__dirname, file));
    db[model.name] = model;
  });

Object.keys(db).forEach(modelName => {
  if (db[modelName].associate) {
    db[modelName].associate(db);
  }
});

db.sequelize = sequelize;
db.Sequelize = Sequelize;

module.exports = db;

import {
  DEV_DATABASE_URL,
  TEST_DATABASE_URL,
  PROD_DATABASE_URL,
} from './contants';

module.exports = {
  development: {
    use_env_variable: true,
    url: DEV_DATABASE_URL,
    dialect: 'postgres',
  },
  test: {
    use_env_variable: true,
    url: TEST_DATABASE_URL,
    dialect: 'postgres',
  },
  production: {
    use_env_variable: true,
    url: PROD_DATABASE_URL,
    dialect: 'postgres',
  },
};

